import React, { useState, useEffect } from 'react';

const ProductsManagement = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [editProduct, setEditProduct] = useState(null);
    const [newProduct, setNewProduct] = useState({
        name: '',
        brand: '',
        category: '',
        product_type: '',
        description: '',
        price: '',
        stock_quantity: '',
        expiry_date: '',
        image: '',
        is_active: true
    });
    const [errorMsg, setErrorMsg] = useState('');

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await fetch('/api/products/admin/products/', {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('access_token')}`
                }
            });
            if (response.ok) {
                const data = await response.json();
                setProducts(data);
            }
        } catch (error) {
            console.error('Error fetching products:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        let productData = editProduct ? editProduct : newProduct;
        
        // Ensure all required fields are present
        const requiredFields = ['name', 'brand', 'category', 'product_type', 'description', 'price', 'stock_quantity', 'expiry_date'];
        const missingFields = requiredFields.filter(field => !productData[field]);
        
        if (missingFields.length > 0) {
            setErrorMsg(`Missing required fields: ${missingFields.join(', ')}`);
            return;
        }

        if (!productData.image) {
            const query = encodeURIComponent(productData.name || 'medicine');
            productData = {
                ...productData,
                image: `https://source.unsplash.com/400x400/?${query}`
            };
        }

        const formData = new FormData();
        Object.keys(productData).forEach(key => {
            if (key !== 'image' || (key === 'image' && productData[key] instanceof File)) {
                formData.append(key, productData[key]);
            } else if (key === 'image' && typeof productData[key] === 'string') {
                formData.append(key, productData[key]);
            }
        });

        try {
            const response = await fetch(
                editProduct ? `/api/products/admin/products/${editProduct.id}/` : '/api/products/admin/products/',
                {
                    method: editProduct ? 'PUT' : 'POST',
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('access_token')}`
                    },
                    body: formData
                }
            );
            
            if (response.ok) {
                fetchProducts();
                if (editProduct) {
                    setEditProduct(null);
                } else {
                    setNewProduct({
                        name: '',
                        brand: '',
                        category: '',
                        product_type: '',
                        description: '',
                        price: '',
                        stock_quantity: '',
                        expiry_date: '',
                        image: '',
                        is_active: true
                    });
                }
                setErrorMsg('');
            } else {
                const errorText = await response.text();
                console.error('Server response:', errorText);
                try {
                    const errorData = JSON.parse(errorText);
                    if (typeof errorData === 'string') {
                        setErrorMsg(errorData);
                    } else if (errorData.error) {
                        setErrorMsg(errorData.error);
                    } else if (typeof errorData === 'object') {
                        setErrorMsg(Object.values(errorData).flat().join(' '));
                    } else {
                        setErrorMsg('Failed to save product.');
                    }
                } catch (parseError) {
                    setErrorMsg(`Server error: ${response.status} ${response.statusText}`);
                }
            }
        } catch (error) {
            setErrorMsg('Network error or server unavailable.');
            console.error('Error saving product:', error);
        }
    };

    const handleDelete = async (productId) => {
        if (window.confirm('Are you sure you want to delete this product?')) {
            try {
                const response = await fetch(`/api/products/admin/products/${productId}/`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('access_token')}`
                    }
                });
                if (response.ok) {
                    fetchProducts();
                }
            } catch (error) {
                console.error('Error deleting product:', error);
            }
        }
    };

    if (loading) {
        return (
            <div className="flex justify-center items-center min-h-screen">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
            </div>
        );
    }

    return (
        <div className="bg-white rounded-lg shadow p-6">
            {errorMsg && (
                <div className="mb-4 p-2 bg-red-100 text-red-700 rounded">{errorMsg}</div>
            )}
            <div className="mb-8">
                <h2 className="text-2xl font-semibold text-gray-800 mb-6">Products Management</h2>
                
                {/* Add New Product Form */}
                <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 gap-4 p-4 bg-gray-50 rounded-lg mb-6">
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Name</label>
                        <input
                            type="text"
                            value={newProduct.name || ''}
                            onChange={(e) => setNewProduct({...newProduct, name: e.target.value})}
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                            required
                        />
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Brand</label>
                        <input
                            type="text"
                            value={newProduct.brand || ''}
                            onChange={(e) => setNewProduct({...newProduct, brand: e.target.value})}
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                            required
                        />
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Category ID</label>
                        <input
                            type="number"
                            value={newProduct.category || ''}
                            onChange={(e) => setNewProduct({...newProduct, category: e.target.value})}
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                            required
                        />
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Product Type</label>
                        <select
                            value={newProduct.product_type || ''}
                            onChange={(e) => setNewProduct({...newProduct, product_type: e.target.value})}
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                            required
                        >
                            <option value="">Select type</option>
                            <option value="medicine">Medicine</option>
                            <option value="supplement">Supplement</option>
                        </select>
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Price</label>
                        <input
                            type="number"
                            step="0.01"
                            value={newProduct.price || ''}
                            onChange={(e) => setNewProduct({...newProduct, price: e.target.value})}
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                            required
                        />
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Stock Quantity</label>
                        <input
                            type="number"
                            value={newProduct.stock_quantity || ''}
                            onChange={(e) => setNewProduct({...newProduct, stock_quantity: e.target.value})}
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                            required
                        />
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Expiry Date</label>
                        <input
                            type="date"
                            value={newProduct.expiry_date || ''}
                            onChange={(e) => setNewProduct({...newProduct, expiry_date: e.target.value})}
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                            required
                        />
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Product Image</label>
                        <input
                            type="file"
                            accept="image/*"
                            onChange={(e) => setNewProduct({...newProduct, image: e.target.files[0]})}
                            className="mt-1 block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-md file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                        />
                    </div>
                    <div className="md:col-span-2">
                        <label className="block text-sm font-medium text-gray-700">Description</label>
                        <textarea
                            value={newProduct.description || ''}
                            onChange={(e) => setNewProduct({...newProduct, description: e.target.value})}
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                            rows="3"
                            required
                        />
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Active</label>
                        <input
                            type="checkbox"
                            checked={newProduct.is_active || false}
                            onChange={(e) => setNewProduct({...newProduct, is_active: e.target.checked})}
                            className="mt-1"
                        />
                    </div>
                    <div className="md:col-span-2">
                        <button
                            type="submit"
                            className="w-full bg-blue-600 text-white py-2 px-4 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
                        >
                            Add New Product
                        </button>
                    </div>
                </form>
            </div>

            {/* Products List */}
            <div className="overflow-x-auto">
                <table className="min-w-full divide-y divide-gray-200">
                    <thead className="bg-gray-50">
                        <tr>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Image</th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Name</th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Price</th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Stock</th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                        </tr>
                    </thead>
                    <tbody className="bg-white divide-y divide-gray-200">
                        {products.map((product) => (
                            <tr key={product.id}>
                                <td className="px-6 py-4 whitespace-nowrap">
                                    <img
                                        src={product.image}
                                        alt={product.name}
                                        className="h-16 w-16 object-cover rounded"
                                    />
                                </td>
                                <td className="px-6 py-4 whitespace-nowrap">{product.name}</td>
                                <td className="px-6 py-4 whitespace-nowrap">${product.price}</td>
                                <td className="px-6 py-4 whitespace-nowrap">{product.stock_quantity}</td>
                                <td className="px-6 py-4 whitespace-nowrap">
                                    <button
                                        onClick={() => setEditProduct({
                                            ...product,
                                            category: product.category?.toString() || '',
                                            price: product.price?.toString() || '',
                                            stock_quantity: product.stock_quantity?.toString() || ''
                                        })}
                                        className="text-blue-600 hover:text-blue-900 mr-4"
                                    >
                                        Edit
                                    </button>
                                    <button
                                        onClick={() => handleDelete(product.id)}
                                        className="text-red-600 hover:text-red-900"
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {/* Edit Product Modal */}
            {editProduct && (
                <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full">
                    <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
                        <div className="flex justify-between items-center mb-4">
                            <h3 className="text-lg font-medium">Edit Product</h3>
                            <button
                                onClick={() => setEditProduct(null)}
                                className="text-gray-500 hover:text-gray-700"
                            >
                                
                            </button>
                        </div>
                        <form onSubmit={handleSubmit}>
                            <div className="space-y-4">
                                <div>
                                    <label className="block text-sm font-medium text-gray-700">Name</label>
                                    <input
                                        type="text"
                                        value={editProduct.name || ''}
                                        onChange={(e) => setEditProduct({
                                            ...editProduct,
                                            name: e.target.value
                                        })}
                                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                                        required
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700">Brand</label>
                                    <input
                                        type="text"
                                        value={editProduct.brand || ''}
                                        onChange={(e) => setEditProduct({
                                            ...editProduct,
                                            brand: e.target.value
                                        })}
                                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                                        required
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700">Category ID</label>
                                    <input
                                        type="number"
                                        value={editProduct.category || ''}
                                        onChange={(e) => setEditProduct({
                                            ...editProduct,
                                            category: e.target.value
                                        })}
                                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                                        required
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700">Product Type</label>
                                    <select
                                        value={editProduct.product_type || ''}
                                        onChange={(e) => setEditProduct({
                                            ...editProduct,
                                            product_type: e.target.value
                                        })}
                                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                                        required
                                    >
                                        <option value="">Select type</option>
                                        <option value="medicine">Medicine</option>
                                        <option value="supplement">Supplement</option>
                                    </select>
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700">Price</label>
                                    <input
                                        type="number"
                                        step="0.01"
                                        value={editProduct.price || ''}
                                        onChange={(e) => setEditProduct({
                                            ...editProduct,
                                            price: e.target.value
                                        })}
                                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                                        required
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700">Stock Quantity</label>
                                    <input
                                        type="number"
                                        value={editProduct.stock_quantity || ''}
                                        onChange={(e) => setEditProduct({
                                            ...editProduct,
                                            stock_quantity: e.target.value
                                        })}
                                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                                        required
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700">Expiry Date</label>
                                    <input
                                        type="date"
                                        value={editProduct.expiry_date || ''}
                                        onChange={(e) => setEditProduct({
                                            ...editProduct,
                                            expiry_date: e.target.value
                                        })}
                                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                                        required
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700">Product Image</label>
                                    <div className="mt-1 flex items-center">
                                        {editProduct.image && typeof editProduct.image === 'string' && (
                                            <div className="mb-2">
                                                <img
                                                    src={editProduct.image}
                                                    alt="Current"
                                                    className="h-20 w-20 object-cover rounded"
                                                />
                                            </div>
                                        )}
                                    </div>
                                    <input
                                        type="file"
                                        accept="image/*"
                                        onChange={(e) => setEditProduct({
                                            ...editProduct,
                                            image: e.target.files[0]
                                        })}
                                        className="mt-1 block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-md file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700">Description</label>
                                    <textarea
                                        value={editProduct.description || ''}
                                        onChange={(e) => setEditProduct({
                                            ...editProduct,
                                            description: e.target.value
                                        })}
                                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                                        rows="3"
                                        required
                                    />
                                </div>
                                <div>
                                    <label className="block text-sm font-medium text-gray-700">Active</label>
                                    <input
                                        type="checkbox"
                                        checked={editProduct.is_active || false}
                                        onChange={(e) => setEditProduct({
                                            ...editProduct,
                                            is_active: e.target.checked
                                        })}
                                        className="mt-1"
                                    />
                                </div>
                            </div>
                            <div className="mt-4 flex justify-end">
                                <button
                                    type="button"
                                    onClick={() => setEditProduct(null)}
                                    className="mr-2 px-4 py-2 text-gray-500 hover:text-gray-700"
                                >
                                    Cancel
                                </button>
                                <button
                                    type="submit"
                                    className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
                                >
                                    Save Changes
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ProductsManagement;
