import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';

const WishlistContext = createContext();

export const useWishlist = () => {
  return useContext(WishlistContext);
};

export const WishlistProvider = ({ children }) => {
  const [wishlist, setWishlist] = useState([]);
  const [loading, setLoading] = useState(true);
  const { isAuthenticated } = useAuth();

  const fetchWishlist = async () => {
    if (!isAuthenticated) {
      setWishlist([]);
      setLoading(false);
      return;
    }
    try {
      const response = await axios.get('/api/products/wishlist/');
      console.log('Fetched wishlist response:', response.data);
      
      // Extract products from wishlist items
      const products = response.data.map(item => item.product);
      console.log('Parsed wishlist products:', products);
      
      setWishlist(products);
    } catch (error) {
      console.error('Error fetching wishlist:', error.response?.data || error.message);
      setWishlist([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchWishlist();
  }, [isAuthenticated]);

  const addToWishlist = async (productId) => {
    try {
      const response = await axios.post(`/api/products/wishlist/add/${productId}/`);
      console.log('Add to wishlist response:', response.data);
      await fetchWishlist();
      return true;
    } catch (error) {
      console.error('Error adding to wishlist:', error.response?.data || error.message);
      return false;
    }
  };

  const removeFromWishlist = async (productId) => {
    try {
      const response = await axios.post(`/api/products/wishlist/remove/${productId}/`);
      console.log('Remove from wishlist response:', response.data);
      await fetchWishlist();
      return true;
    } catch (error) {
      console.error('Error removing from wishlist:', error.response?.data || error.message);
      return false;
    }
  };

  const isInWishlist = (productId) => {
    console.log('Checking if product is in wishlist:', productId, wishlist);
    return wishlist.some(product => 
      product.id === productId || product.product_id === productId
    );
  };

  const value = {
    wishlist,
    loading,
    addToWishlist,
    removeFromWishlist,
    isInWishlist,
  };

  return (
    <WishlistContext.Provider value={value}>
      {children}
    </WishlistContext.Provider>
  );
};
