import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Subscriptions = () => {
  const [subscriptions, setSubscriptions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchSubscriptions();
  }, []);

  const fetchSubscriptions = async () => {
    try {
      const response = await axios.get('/api/products/subscriptions/');
      setSubscriptions(response.data);
    } catch (err) {
      setError('Failed to fetch subscriptions');
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = async (id) => {
    if (!window.confirm('Cancel this subscription?')) return;
    try {
      await axios.delete(`/api/products/subscriptions/${id}/`);
      setSubscriptions(subscriptions.filter(sub => sub.id !== id));
      alert('Subscription cancelled');
    } catch (err) {
      alert('Failed to cancel subscription');
    }
  };

  if (loading) return <div className="p-8 text-center">Loading subscriptions...</div>;
  if (error) return <div className="p-8 text-center text-red-600">{error}</div>;

  return (
    <div className="card p-8 max-w-2xl mx-auto">
      <h2 className="text-2xl font-bold mb-6">My Subscriptions</h2>
      {subscriptions.length === 0 ? (
        <p className="text-gray-500">You have no active subscriptions.</p>
      ) : (
        <div className="space-y-4">
          {subscriptions.map(sub => (
            <div key={sub.id} className="border rounded-lg p-4 flex flex-col md:flex-row md:items-center justify-between">
              <div>
                <div className="font-semibold text-lg">{sub.product_name}</div>
                <div className="text-gray-600">Frequency: {sub.frequency}</div>
                <div className="text-gray-600">Quantity: {sub.quantity}</div>
                <div className="text-gray-600">Next Delivery: {sub.next_delivery}</div>
                <div className="text-gray-600">Status: {sub.is_active ? 'Active' : 'Cancelled'}</div>
              </div>
              {sub.is_active && (
                <button
                  className="btn-secondary mt-4 md:mt-0"
                  onClick={() => handleCancel(sub.id)}
                >
                  Cancel Subscription
                </button>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Subscriptions;
