import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminReviewModeration = () => {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchReviews();
  }, []);

  const fetchReviews = async () => {
    try {
      const response = await axios.get('/api/reviews/my-reviews/?all=true'); // You may need a dedicated admin endpoint
      setReviews(response.data.results || response.data);
    } catch (error) {
      console.error('Failed to fetch reviews:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleApprove = async (reviewId) => {
    try {
      await axios.post(`/api/reviews/admin/${reviewId}/approve/`);
      fetchReviews();
    } catch (error) {
      alert('Failed to approve review');
    }
  };

  const handleHide = async (reviewId) => {
    try {
      await axios.post(`/api/reviews/admin/${reviewId}/hide/`);
      fetchReviews();
    } catch (error) {
      alert('Failed to hide review');
    }
  };

  if (loading) {
    return <div className="flex justify-center items-center min-h-64"><div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div></div>;
  }

  return (
    <div className="card mt-8">
      <h3 className="text-xl font-semibold mb-4">Review Moderation</h3>
      {reviews.length > 0 ? (
        <div className="space-y-4">
          {reviews.map((review) => (
            <div key={review.id} className="border-b pb-4">
              <div className="flex justify-between items-center mb-2">
                <span className="font-semibold">{review.user_name}</span>
                <span className={`px-2 py-1 rounded text-xs ${review.is_approved ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>
                  {review.is_approved ? 'Approved' : 'Hidden'}
                </span>
              </div>
              <div className="flex items-center mb-2">
                {[...Array(5)].map((_, i) => (
                  <svg key={i} className={`w-4 h-4 ${i < review.rating ? 'text-yellow-400' : 'text-gray-300'}`} fill="currentColor" viewBox="0 0 20 20">
                    <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                  </svg>
                ))}
              </div>
              <h4 className="font-semibold mb-1">{review.title}</h4>
              <p className="text-gray-700 mb-2">{review.comment}</p>
              <div className="flex space-x-2">
                {!review.is_approved && (
                  <button onClick={() => handleApprove(review.id)} className="btn-primary">Approve</button>
                )}
                {review.is_approved && (
                  <button onClick={() => handleHide(review.id)} className="btn-secondary">Hide</button>
                )}
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-gray-500">No reviews to moderate</p>
      )}
    </div>
  );
};

export default AdminReviewModeration;
