import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';

const ProductDetail = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [relatedProducts, setRelatedProducts] = useState([]);
  const [quantity, setQuantity] = useState(1);
  const [loading, setLoading] = useState(true);
  const [reviewLoading, setReviewLoading] = useState(false);
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [reviewForm, setReviewForm] = useState({
    rating: 5,
    title: '',
    comment: '',
    image: null,
    video: null
  });
  const [showSubscriptionForm, setShowSubscriptionForm] = useState(false);
  const [subscriptionForm, setSubscriptionForm] = useState({
    frequency: 'monthly',
    quantity: 1
  });
  const [submittingSubscription, setSubmittingSubscription] = useState(false);

  const { addToCart } = useCart();
  const { isAuthenticated } = useAuth();

  useEffect(() => {
    fetchProduct();
    fetchReviews();
    fetchRelatedProducts();
  }, [id]);
  const fetchRelatedProducts = async () => {
    try {
      const response = await axios.get(`/api/products/${id}/related/`);
      setRelatedProducts(response.data);
    } catch (error) {
      console.error('Failed to fetch related products:', error);
    }
  };

  const fetchProduct = async () => {
    try {
      const response = await axios.get(`/api/products/${id}/`);
      setProduct(response.data);
    } catch (error) {
      console.error('Failed to fetch product:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchReviews = async () => {
    try {
      const response = await axios.get(`/api/reviews/product/${id}/`);
      setReviews(response.data.results || response.data);
    } catch (error) {
      console.error('Failed to fetch reviews:', error);
    }
  };

  const handleAddToCart = async () => {
    const result = await addToCart(product.id, quantity);
    if (result.success) {
      alert('Product added to cart!');
    } else {
      alert(result.error);
    }
  };

  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    setReviewLoading(true);
    try {
      const formData = new FormData();
      formData.append('product_id', product.id);
      formData.append('rating', reviewForm.rating);
      formData.append('title', reviewForm.title);
      formData.append('comment', reviewForm.comment);
      if (reviewForm.image) formData.append('image', reviewForm.image);
      if (reviewForm.video) formData.append('video', reviewForm.video);
      await axios.post('/api/reviews/create/', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      setShowReviewForm(false);
      setReviewForm({ rating: 5, title: '', comment: '', image: null, video: null });
      fetchReviews();
      fetchProduct();
      alert('Review submitted successfully!');
    } catch (error) {
      alert(error.response?.data?.error || 'Failed to submit review');
    } finally {
      setReviewLoading(false);
    }
  }

  const handleSubscriptionSubmit = async (e) => {
    e.preventDefault();
    setSubmittingSubscription(true);
    try {
      await axios.post('/api/products/subscriptions/', {
        product: product.id,
        quantity: subscriptionForm.quantity,
        frequency: subscriptionForm.frequency,
        next_delivery: new Date().toISOString().slice(0, 10)
      });
      setShowSubscriptionForm(false);
      setSubscriptionForm({ frequency: 'monthly', quantity: 1 });
      alert('Subscription created successfully!');
    } catch (error) {
      alert(error.response?.data?.error || 'Failed to create subscription');
    } finally {
      setSubmittingSubscription(false);
    }
  };

  if (loading) {
    return (
      <div>
        <div className="flex justify-center items-center min-h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
        </div>
        {/* Related Products Section */}
        {relatedProducts.length > 0 && (
          <div className="card mt-8">
            <h3 className="text-xl font-semibold mb-4">Related Products</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              {relatedProducts.map((rp) => (
                <Link key={rp.id} to={`/products/${rp.id}`} className="block border rounded-lg p-4 hover:shadow-lg transition-shadow">
                  <div className="aspect-square bg-gray-100 rounded flex items-center justify-center mb-2">
                    {rp.image ? (
                      <img src={rp.image} alt={rp.name} className="w-full h-full object-cover rounded" />
                    ) : (
                      <svg className="w-16 h-16 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19.428 15.428a2 2 0 00-1.022-.547l-2.387-.477a6 6 0 00-3.86.517l-.318.158a6 6 0 01-3.86.517L6.05 15.21a2 2 0 00-1.806.547M8 4h8l-1 1v5.172a2 2 0 00.586 1.414l5 5c1.26 1.26.367 3.414-1.415 3.414H4.828c-1.782 0-2.674-2.154-1.414-3.414l5-5A2 2 0 009 8.172V5L8 4z" />
                      </svg>
                    )}
                  </div>
                  <div className="font-bold text-lg">{rp.name}</div>
                  <div className="text-gray-600">{rp.brand}</div>
                  <div className="text-blue-600 font-semibold">${rp.price}</div>
                </Link>
              ))}
            </div>
          </div>
        )}
      </div>
    );
  }

  if (!product) {
    return (
      <div className="text-center py-12">
        <h2 className="text-2xl font-bold text-gray-600">Product not found</h2>
        <Link to="/products" className="btn-primary mt-4 inline-block">
          Back to Products
        </Link>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Product Details */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        <div>
          <div className="aspect-square bg-gray-200 rounded-lg flex items-center justify-center">
            {product.image ? (
              <img
                src={product.image}
                alt={product.name}
                className="w-full h-full object-cover rounded-lg"
              />
            ) : (
              <svg className="w-32 h-32 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19.428 15.428a2 2 0 00-1.022-.547l-2.387-.477a6 6 0 00-3.86.517l-.318.158a6 6 0 01-3.86.517L6.05 15.21a2 2 0 00-1.806.547M8 4h8l-1 1v5.172a2 2 0 00.586 1.414l5 5c1.26 1.26.367 3.414-1.415 3.414H4.828c-1.782 0-2.674-2.154-1.414-3.414l5-5A2 2 0 009 8.172V5L8 4z" />
              </svg>
            )}
          </div>
        </div>

        <div className="space-y-6">
          <div>
            <h1 className="text-3xl font-bold">{product.name}</h1>
            <p className="text-xl text-gray-600">{product.brand}</p>
            <p className="text-gray-500">{product.category_name}</p>
          </div>

          <div className="flex items-center space-x-4">
            <div className="flex items-center">
              {[...Array(5)].map((_, i) => (
                <svg
                  key={i}
                  className={`w-5 h-5 ${
                    i < Math.floor(product.average_rating)
                      ? 'text-yellow-400'
                      : 'text-gray-300'
                  }`}
                  fill="currentColor"
                  viewBox="0 0 20 20"
                >
                  <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                </svg>
              ))}
            </div>
            <span className="text-gray-600">
              {(product.average_rating ? product.average_rating.toFixed(1) : "0.0")} ({product.review_count ?? 0} reviews)
            </span>
          </div>

          <div className="text-4xl font-bold text-blue-600">
            ${product.price}
          </div>

          <div className="space-y-2">
            <div className={`inline-block px-3 py-1 rounded text-sm ${
              product.is_in_stock
                ? 'bg-green-100 text-green-800'
                : 'bg-red-100 text-red-800'
            }`}>
              {product.is_in_stock ? `${product.stock_quantity} in stock` : 'Out of stock'}
            </div>

            {product.is_low_stock && product.is_in_stock && (
              <div className="block text-orange-600 text-sm">
                Only {product.stock_quantity} left!
              </div>
            )}

            {product.days_to_expiry <= 30 && (
              <div className="block text-red-600 text-sm">
                Expires in {product.days_to_expiry} days
              </div>
            )}
          </div>

          {product.is_in_stock && (
            <div className="flex items-center space-x-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Quantity
                </label>
                <select
                  value={quantity}
                  onChange={(e) => setQuantity(parseInt(e.target.value))}
                  className="input-field w-20"
                >
                  {[...Array(Math.min(10, product.stock_quantity))].map((_, i) => (
                    <option key={i + 1} value={i + 1}>
                      {i + 1}
                    </option>
                  ))}
                </select>
              </div>
              <button
                onClick={handleAddToCart}
                className="btn-primary"
              >
                Add to Cart
              </button>
            </div>
          )}

          {product.requires_prescription && (
            <div className="bg-yellow-100 border border-yellow-400 text-yellow-700 px-4 py-3 rounded">
              <strong>Prescription Required:</strong> This medicine requires a valid prescription.
            </div>
          )}
        </div>
      </div>

      {/* Product Information */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        <div className="card">
          <h3 className="text-xl font-semibold mb-4">Description</h3>
          <p className="text-gray-700">{product.description}</p>

          {product.dosage && (
            <div className="mt-4">
              <h4 className="font-semibold">Dosage:</h4>
              <p className="text-gray-700">{product.dosage}</p>
            </div>
          )}
        </div>

        {product.precautions && (
          <div className="card">
            <h3 className="text-xl font-semibold mb-4">Precautions</h3>
            <p className="text-gray-700">{product.precautions}</p>
          </div>
        )}
      </div>

      {/* Reviews Section */}
      <div className="card">
        <div className="flex justify-between items-center mb-6">
          <h3 className="text-xl font-semibold">Customer Reviews</h3>
          {isAuthenticated && (
            <button
              onClick={() => setShowReviewForm(!showReviewForm)}
              className="btn-primary"
            >
              Write a Review
            </button>
          )}
        </div>

        {/* Review Form */}
        {showReviewForm && (
          <form onSubmit={handleReviewSubmit} className="mb-8 p-4 bg-gray-50 rounded-lg">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Rating
                </label>
                <select
                  value={reviewForm.rating}
                  onChange={(e) => setReviewForm({...reviewForm, rating: parseInt(e.target.value)})}
                  className="input-field"
                >
                  {[5, 4, 3, 2, 1].map(rating => (
                    <option key={rating} value={rating}>
                      {rating} Star{rating !== 1 ? 's' : ''}
                    </option>
                  ))}
                </select>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Title
                </label>
                <input
                  type="text"
                  value={reviewForm.title}
                  onChange={(e) => setReviewForm({...reviewForm, title: e.target.value})}
                  className="input-field"
                  placeholder="Review title"
                />
              </div>
            </div>
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Comment
              </label>
              <textarea
                value={reviewForm.comment}
                onChange={(e) => setReviewForm({...reviewForm, comment: e.target.value})}
                className="input-field"
                rows="4"
                placeholder="Share your experience with this product"
              />
            </div>
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">Image (optional)</label>
              <input
                type="file"
                accept="image/*"
                onChange={e => setReviewForm({...reviewForm, image: e.target.files[0]})}
                className="input-field"
              />
            </div>
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">Video (optional)</label>
              <input
                type="file"
                accept="video/*"
                onChange={e => setReviewForm({...reviewForm, video: e.target.files[0]})}
                className="input-field"
              />
            </div>
            <div className="flex space-x-2">
              <button
                type="submit"
                disabled={reviewLoading}
                className="btn-primary disabled:opacity-50"
              >
                {reviewLoading ? 'Submitting...' : 'Submit Review'}
              </button>
              <button
                type="button"
                onClick={() => setShowReviewForm(false)}
                className="btn-secondary"
              >
                Cancel
              </button>
            </div>
          </form>
        )}

        {/* Reviews List */}
        <div className="space-y-4">
          {reviews.length > 0 ? (
            reviews.map((review) => (
              <div key={review.id} className="border-b pb-4">
                <div className="flex items-center justify-between mb-2">
                  <div className="flex items-center space-x-2">
                    <span className="font-semibold">{review.user_name}</span>
                    {review.is_verified_purchase && (
                      <span className="bg-green-100 text-green-800 text-xs px-2 py-1 rounded">
                        Verified Purchase
                      </span>
                    )}
                  </div>
                  <div className="flex items-center">
                    {[...Array(5)].map((_, i) => (
                      <svg
                        key={i}
                        className={`w-4 h-4 ${
                          i < review.rating ? 'text-yellow-400' : 'text-gray-300'
                        }`}
                        fill="currentColor"
                        viewBox="0 0 20 20"
                      >
                        <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                      </svg>
                    ))}
                  </div>
                </div>
                {review.title && (
                  <h4 className="font-semibold mb-1">{review.title}</h4>
                )}
                <p className="text-gray-700 mb-2">{review.comment}</p>
                {/* Display review media */}
                {(review.image || review.video) && (
                  <div className="mt-2">
                    {review.image && (
                      <img src={review.image} alt="Review" className="max-w-xs rounded mb-2" />
                    )}
                    {review.video && (
                      <video controls className="max-w-xs rounded">
                        <source src={review.video} type="video/mp4" />
                        Your browser does not support the video tag.
                      </video>
                    )}
                  </div>
                )}
                <div className="flex items-center justify-between text-sm text-gray-500">
                  <span>{new Date(review.created_at).toLocaleDateString()}</span>
                  <div className="flex items-center space-x-4">
                    <span>Helpful: {review.helpful_count}</span>
                    <span>Not Helpful: {review.not_helpful_count}</span>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <p className="text-gray-500 text-center py-8">
              No reviews yet. Be the first to review this product!
            </p>
          )}
        </div>
      </div>

      {/* Subscription Section */}
      {isAuthenticated && (
        <div className="card mt-6">
          <h3 className="text-lg font-semibold mb-2">Subscribe to this Medicine</h3>
          <button
            className="btn-primary mb-4"
            onClick={() => setShowSubscriptionForm(!showSubscriptionForm)}
          >
            {showSubscriptionForm ? 'Hide Subscription Options' : 'Subscribe'}
          </button>
          {showSubscriptionForm && (
            <form onSubmit={handleSubscriptionSubmit} className="space-y-4">
              <div>
                <label className="block text-sm font-medium mb-1">Frequency</label>
                <select
                  value={subscriptionForm.frequency}
                  onChange={e => setSubscriptionForm({ ...subscriptionForm, frequency: e.target.value })}
                  className="input-field"
                >
                  <option value="weekly">Weekly</option>
                  <option value="biweekly">Biweekly</option>
                  <option value="monthly">Monthly</option>
                  <option value="yearly">Yearly</option>
                </select>
              </div>
              <div>
                <label className="block text-sm font-medium mb-1">Quantity</label>
                <input
                  type="number"
                  min="1"
                  max={product.stock_quantity}
                  value={subscriptionForm.quantity}
                  onChange={e => setSubscriptionForm({ ...subscriptionForm, quantity: parseInt(e.target.value) })}
                  className="input-field"
                />
              </div>
              <button
                type="submit"
                className="btn-primary"
                disabled={submittingSubscription}
              >
                {submittingSubscription ? 'Subscribing...' : 'Start Subscription'}
              </button>
            </form>
          )}
        </div>
      )}
    </div>
  );
};

export default ProductDetail;
