import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminAnalyticsDashboard = () => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const response = await axios.get('/api/products/admin/analytics/');
      setStats(response.data);
    } catch (err) {
      setError('Failed to fetch analytics');
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div className="p-8 text-center">Loading analytics...</div>;
  if (error) return <div className="p-8 text-center text-red-600">{error}</div>;

  return (
    <div className="card p-8 max-w-2xl mx-auto">
      <h2 className="text-2xl font-bold mb-6">Admin Analytics Dashboard</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="bg-blue-50 p-4 rounded-lg">
          <h3 className="font-semibold mb-2">Sales</h3>
          <p>Total Sales: <span className="font-bold">${stats.total_sales.toFixed(2)}</span></p>
          <p>Total Orders: <span className="font-bold">{stats.total_orders}</span></p>
        </div>
        <div className="bg-green-50 p-4 rounded-lg">
          <h3 className="font-semibold mb-2">Inventory</h3>
          <p>Total Products: <span className="font-bold">{stats.total_products}</span></p>
          <p>Low Stock: <span className="font-bold text-orange-600">{stats.low_stock}</span></p>
          <p>Expiring Soon: <span className="font-bold text-red-600">{stats.expiring_soon}</span></p>
        </div>
        <div className="bg-purple-50 p-4 rounded-lg">
          <h3 className="font-semibold mb-2">Users</h3>
          <p>Total Users: <span className="font-bold">{stats.total_users}</span></p>
          <p>Active Users: <span className="font-bold">{stats.active_users}</span></p>
        </div>
      </div>
    </div>
  );
};

export default AdminAnalyticsDashboard;
