import React, { createContext, useContext, useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';

const WishlistContext = createContext();

export const useWishlist = () => {
  return useContext(WishlistContext);
};

export const WishlistProvider = ({ children }) => {
  const [wishlist, setWishlist] = useState([]);
  const [loading, setLoading] = useState(true);
  const { isAuthenticated } = useAuth();

  const fetchWishlist = useCallback(async () => {
    if (!isAuthenticated) {
      setWishlist([]);
      setLoading(false);
      return;
    }
    try {
      const response = await axios.get('/api/products/wishlist/');
      console.log('Wishlist API response:', response.data);

      // Handle paginated response format
      if (response.data && response.data.results && Array.isArray(response.data.results)) {
        // Extract products from paginated wishlist items
        const products = response.data.results.map(item => item.product).filter(product => product);
        console.log('Extracted products:', products);
        setWishlist(products);
      } else if (Array.isArray(response.data)) {
        // Handle direct array response
        const products = response.data.map(item => item.product).filter(product => product);
        console.log('Extracted products:', products);
        setWishlist(products);
      } else {
        console.error('Unexpected wishlist response format:', response.data);
        setWishlist([]);
      }
    } catch (error) {
      console.error('Error fetching wishlist:', error.response?.data || error.message);
      setWishlist([]);
    } finally {
      setLoading(false);
    }
  }, [isAuthenticated]);

  useEffect(() => {
    fetchWishlist();
  }, [isAuthenticated]);

  const addToWishlist = async (productId) => {
    try {
      const response = await axios.post(`/api/products/wishlist/add/${productId}/`);
      console.log('Add to wishlist response:', response.data);
      await fetchWishlist();
      return true;
    } catch (error) {
      console.error('Error adding to wishlist:', error.response?.data || error.message);
      return false;
    }
  };

  const removeFromWishlist = async (productId) => {
    try {
      const response = await axios.post(`/api/products/wishlist/remove/${productId}/`);
      console.log('Remove from wishlist response:', response.data);
      await fetchWishlist();
      return true;
    } catch (error) {
      console.error('Error removing from wishlist:', error.response?.data || error.message);
      return false;
    }
  };

  const isInWishlist = (productId) => {
    return wishlist.some(product => product.id === productId);
  };

  const value = {
    wishlist,
    loading,
    addToWishlist,
    removeFromWishlist,
    isInWishlist,
  };

  return (
    <WishlistContext.Provider value={value}>
      {children}
    </WishlistContext.Provider>
  );
};
